import React, { useState } from 'react';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);

  // Mock video data - replace with actual API calls
  const mockVideos = [
    { id: 1, title: 'Sample Video 1', thumbnail: '/thumb1.jpg', duration: '10:30' },
    { id: 2, title: 'Sample Video 2', thumbnail: '/thumb2.jpg', duration: '15:45' },
    { id: 3, title: 'Sample Video 3', thumbnail: '/thumb3.jpg', duration: '22:15' }
  ];

  return (
    <div className="App">
      <header className="App-header">
        <h1>üé¨ MERN Mediastream</h1>
        <p>Your Professional Media Streaming Platform</p>
      </header>

      <div className="main-container">
        {/* Video Player Section */}
        <div className="video-section">
          {selectedVideo ? (
            <div className="video-player">
              <h3>Now Playing: {selectedVideo.title}</h3>
              <div className="video-placeholder">
                <p>Video Player Will Appear Here</p>
                <p>üì∫ Streaming: {selectedVideo.title}</p>
              </div>
              <button 
                className="back-btn"
                onClick={() => setSelectedVideo(null)}
              >
                ‚Üê Back to Videos
              </button>
            </div>
          ) : (
            <div className="video-grid">
              <h2>üé• Available Videos</h2>
              <div className="videos-container">
                {mockVideos.map(video => (
                  <div 
                    key={video.id} 
                    className="video-card"
                    onClick={() => setSelectedVideo(video)}
                  >
                    <div className="thumbnail">
                      <div className="thumbnail-placeholder">
                        üé¨
                      </div>
                      <span className="duration">{video.duration}</span>
                    </div>
                    <div className="video-info">
                      <h4>{video.title}</h4>
                      <button className="play-btn">‚ñ∂ Play</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Upload Section */}
        <div className="upload-section">
          <h3>üì§ Upload New Media</h3>
          <div className="upload-area">
            <p>Drag & drop video files here or</p>
            <button className="upload-btn">Choose File</button>
            <p className="file-types">Supported: MP4, AVI, MOV, WMV</p>
          </div>
        </div>
      </div>

      <footer className="app-footer">
        <p>MERN Mediastream &copy; 2025 - Professional Streaming Solution</p>
      </footer>
    </div>
  );
}

export default App;
